import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { BehaviorSubject } from 'rxjs';
import { UserService } from '../../user/user.service';

@Component({
  selector: 'app-nav-home',
  templateUrl: './nav-home.component.html',
  styleUrls: ['./nav-home.component.css']
})
export class NavHomeComponent implements OnInit {
  isLoggedIn = new BehaviorSubject<boolean>(false);
  userName = new BehaviorSubject<string>(this.UserName());
  constructor(private router: Router, private UserService: UserService) { }

  ngOnInit(): void {
    this.isLoggedIn = this.UserService.getLoginStatus;
  }

  loginStatus(): boolean {
    if (localStorage.getItem('IsLoggedIn')) {
      return true;
    }
    return false;
  }

  logout() {
    localStorage.removeItem('token');
    localStorage.removeItem('UserName');
    localStorage.setItem('IsLoggedIn', "false");
    this.router.navigateByUrl('/');
  }

  getLoginStatus() {
    return this.isLoggedIn;
  }

  getUserName(): any {
    return this.userName;
  }

  UserName(): string {
    let userNameString = localStorage.getItem('UserName')!;
    if (localStorage.getItem('UserName') !== null) {
      return userNameString;
    } else {
      return "";
    }
  }
}
<header>
  <nav class="navbar navbar-expand-sm navbar-toggleable-sm navbar-light bg-white border-bottom box-shadow mb-3">
    <a class="navbar-brand" routerLink="/">PropertyDimash</a>
    <button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target=".navbar-collapse" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
      <span class="navbar-toggler-icon"></span>
    </button>
    <div class="navbar-collapse navbar-collapse d-sm-inline-flex justify-content-between">
      <ul class="navbar-nav flew-grow-1">
        <li class="nav-item">
          <a class="nav-link text-dark" routerLink="/properties">Property and Real estate</a>
        </li>
        <li class="nav-item">
          <a class="nav-link text-dark" routerLink="/residential-complexes/index">ResidentialComplexes</a>
        </li>
        <li class="nav-item">
          <a class="nav-link text-dark" routerLink="/news/index">News and Property</a>
        </li>
      </ul>
      <ul class="navbar-nav">
        <li class="nav-item" *ngIf="isLoggedIn | async">
          <a class="nav-link text-dark" routerLink="/user/registration">Sign Up</a>
        </li>
        <li class="nav-item">
          <a class="nav-link text-dark" routerLink="/user/login">Log In</a>
        </li>
        <li class="nav-item">
          <a class="nav-link text-dark" (click)="logout()">Logout</a>
        </li>
      </ul>
    </div>
  </nav>
</header>
